import React, { useState } from "react";
import TriageLabel from "./QrCodeGenerator";

export default function QrCodeTestPage() {
  const [testCaseId, setTestCaseId] = useState("TEST-001");
  const [testStatus, setTestStatus] = useState("Test durumu: Hastanın durumu stabil");
  const [testArea, setTestArea] = useState("ACİL SERVİS");
  const [testDepartment, setTestDepartment] = useState("GENEL CERRAHİ");
  const [testBaseUrl, setTestBaseUrl] = useState("https://aitriaj.com");
  const [testPath, setTestPath] = useState("/triage/case_id");

  const testCases = [
    {
      id: "TEST-001",
      status: "Hastanın 'bayılma' ifadesi ciddi ve acil müdahale gerektiren durumları düşündürür.",
      area: "ACİL SERVİS",
      department: "GENEL CERRAHİ"
    },
    {
      id: "TEST-002", 
      status: "Hasta bilinçli, vital bulgular stabil",
      area: "POLİKLİNİK",
      department: "İÇ HASTALIKLARI"
    },
    {
      id: "TEST-003",
      status: "Acil müdahale gerekli - kritik durum",
      area: "YOĞUN BAKIM",
      department: "ANESTEZİ VE REANİMASYON"
    }
  ];

  const handleTestCaseSelect = (testCase: any) => {
    setTestCaseId(testCase.id);
    setTestStatus(testCase.status);
    setTestArea(testCase.area);
    setTestDepartment(testCase.department);
  };

  return (
    <div className="qr-test-page">
      <div className="qr-test-container">
        <h1 className="qr-test-title">
          QR Kod Test Sayfası
        </h1>
        
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Sol taraf - Kontroller */}
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h2 className="text-xl font-semibold mb-4 text-gray-700">
              Test Parametreleri
            </h2>
            
            {/* Hazır Test Durumları */}
            <div className="mb-6">
              <h3 className="text-lg font-medium mb-3 text-gray-600">
                Hazır Test Durumları
              </h3>
              <div className="space-y-2">
                {testCases.map((testCase, index) => (
                  <button
                    key={index}
                    onClick={() => handleTestCaseSelect(testCase)}
                    className={`w-full text-left p-3 rounded border transition-colors ${
                      testCaseId === testCase.id
                        ? 'bg-blue-100 border-blue-300 text-blue-800'
                        : 'bg-gray-50 border-gray-200 hover:bg-gray-100'
                    }`}
                  >
                    <div className="font-medium">{testCase.id}</div>
                    <div className="text-sm text-gray-600">{testCase.area} - {testCase.department}</div>
                  </button>
                ))}
              </div>
            </div>

            {/* Manuel Parametreler */}
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Case ID
                </label>
                <input
                  type="text"
                  value={testCaseId}
                  onChange={(e) => setTestCaseId(e.target.value)}
                  className="w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Durum
                </label>
                <textarea
                  value={testStatus}
                  onChange={(e) => setTestStatus(e.target.value)}
                  rows={3}
                  className="w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Alan
                </label>
                <input
                  type="text"
                  value={testArea}
                  onChange={(e) => setTestArea(e.target.value)}
                  className="w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Bölüm
                </label>
                <input
                  type="text"
                  value={testDepartment}
                  onChange={(e) => setTestDepartment(e.target.value)}
                  className="w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Base URL
                </label>
                <input
                  type="text"
                  value={testBaseUrl}
                  onChange={(e) => setTestBaseUrl(e.target.value)}
                  className="w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Path
                </label>
                <input
                  type="text"
                  value={testPath}
                  onChange={(e) => setTestPath(e.target.value)}
                  className="w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>
            </div>
          </div>

          {/* Sağ taraf - QR Kod Önizleme */}
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h2 className="text-xl font-semibold mb-4 text-gray-700">
              QR Kod Önizleme
            </h2>
            
            <div className="flex justify-center mb-4">
              <TriageLabel
                caseId={testCaseId}
                status={testStatus}
                area={testArea}
                department={testDepartment}
                baseUrl={testBaseUrl}
                path={testPath}
              />
            </div>

            {/* QR Kod Bilgileri */}
            <div className="mt-6 p-4 bg-gray-50 rounded">
              <h3 className="font-medium mb-2 text-gray-700">QR Kod Bilgileri:</h3>
              <div className="text-sm space-y-1">
                <div><strong>URL:</strong> {testBaseUrl}{testPath.replace("case_id", testCaseId)}</div>
                <div><strong>Case ID:</strong> {testCaseId}</div>
                <div><strong>Alan:</strong> {testArea}</div>
                <div><strong>Bölüm:</strong> {testDepartment}</div>
              </div>
            </div>

            {/* Test Butonları */}
            <div className="mt-6 space-y-2">
              <button
                onClick={() => {
                  const qrUrl = `${testBaseUrl}${testPath.replace("case_id", testCaseId)}`;
                  window.open(qrUrl, '_blank');
                }}
                className="w-full bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700 transition-colors"
              >
                QR Kod Linkini Aç
              </button>
              
              <button
                onClick={() => {
                  const qrUrl = `${testBaseUrl}${testPath.replace("case_id", testCaseId)}`;
                  navigator.clipboard.writeText(qrUrl);
                  alert('QR kod URL\'si panoya kopyalandı!');
                }}
                className="w-full bg-green-600 text-white py-2 px-4 rounded hover:bg-green-700 transition-colors"
              >
                URL'yi Panoya Kopyala
              </button>
            </div>
          </div>
        </div>

        {/* Alt kısım - Bilgi */}
        <div className="mt-8 bg-blue-50 border border-blue-200 rounded-lg p-4">
          <h3 className="font-medium text-blue-800 mb-2">Test Sayfası Hakkında</h3>
          <p className="text-blue-700 text-sm">
            Bu sayfa QR Kod Generator bileşeninin farklı parametrelerle nasıl çalıştığını test etmek için oluşturulmuştur. 
            Sol taraftaki kontrollerle parametreleri değiştirebilir, sağ tarafta sonucu anlık olarak görebilirsiniz.
          </p>
        </div>
      </div>
    </div>
  );
}
