import React, { useState, useCallback } from 'react';
import './App.css';
import Header from './components/Header';
import InputSection from './components/InputSection';
import ResultsDisplay from './components/ResultsDisplay';
import { TriageState, TriageInput, TriageOutput } from './types/TriageTypes';
import { triageApi } from './services/triageApi';

function App() {
  const [triageState, setTriageState] = useState<TriageState>({
    caseId: null,
    currentQuestion: null,
    remainingQuestions: 0,
    triage: null,
    filePath: null,
    isLoading: false,
    error: null,
  });

  const [triageResults, setTriageResults] = useState<Array<{
    caseId: string;
    triage: TriageOutput;
    timestamp: string;
    patientName: string;
  }>>([]);

  const handleStartTriage = useCallback(async (input: TriageInput) => {
    setTriageState(prev => ({ ...prev, isLoading: true, error: null }));

    try {
      const response = await triageApi.startTriage(input);

      setTriageState(prev => ({
        ...prev,
        caseId: response.case_id,
        currentQuestion: null,
        remainingQuestions: 0,
        triage: response.triage,
        filePath: response.file_path,
        isLoading: false,
        error: null,
      }));

      // Add to results list
      const newResult = {
        caseId: response.case_id,
        triage: response.triage,
        timestamp: new Date().toLocaleString('tr-TR'),
        patientName: input.name
      };

      setTriageResults(prev => [newResult, ...prev]);

      return response;
    } catch (error: any) {
      setTriageState(prev => ({
        ...prev,
        isLoading: false,
        error: error.message,
      }));
      throw error;
    }
  }, []);

  const handleSendAnswer = useCallback(async (answer: string, skip: boolean = false) => {
    if (!triageState.caseId || !triageState.currentQuestion) return;

    setTriageState(prev => ({ ...prev, isLoading: true, error: null }));

    try {
      const body = {
        answers: { [triageState.currentQuestion]: skip ? '' : answer },
        done: false,
      };

      const response = await triageApi.sendAnswer(triageState.caseId, body);

      setTriageState(prev => ({
        ...prev,
        currentQuestion: null,
        remainingQuestions: 0,
        triage: response.triage,
        isLoading: false,
        error: null,
      }));

      // Update the result in the list
      setTriageResults(prev => 
        prev.map(result => 
          result.caseId === triageState.caseId 
            ? { ...result, triage: response.triage }
            : result
        )
      );

      return response;
    } catch (error: any) {
      setTriageState(prev => ({
        ...prev,
        isLoading: false,
        error: error.message,
      }));
      throw error;
    }
  }, [triageState.caseId, triageState.currentQuestion]);

  const handleDone = useCallback(() => {
    setTriageState(prev => ({ ...prev, currentQuestion: null, remainingQuestions: 0 }));
  }, []);

  const handleReset = useCallback(() => {
    setTriageState({
      caseId: null,
      currentQuestion: null,
      remainingQuestions: 0,
      triage: null,
      filePath: null,
      isLoading: false,
      error: null,
    });
  }, []);

  return (
    <div className="App">
      <Header />
      <main className="main-content">
        <div className="dashboard-container">
          <div className="input-panel">
            <InputSection
              triageState={triageState}
              onStartTriage={handleStartTriage}
              onSendAnswer={handleSendAnswer}
              onDone={handleDone}
              onReset={handleReset}
            />
          </div>

          <ResultsDisplay results={triageResults} isLoading={triageState.isLoading} />
        </div>
      </main>
    </div>
  );
}

export default App;
