import React, { useState } from 'react';
import './PatientEntry.css';

interface PatientEntryProps {
  onStartAssessment: (patientData: any) => Promise<void>;
}

const PatientEntry: React.FC<PatientEntryProps> = ({ onStartAssessment }) => {
  const [currentStep, setCurrentStep] = useState(1);
  const [isLoading, setIsLoading] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    age: '30',
    gender: 'F',
    sex: 'F',
    complaint: '',
    pregnancy: 'any',
    chief: '',
    vitals: ''
  });

  const steps = [
    { id: 1, title: 'Hasta Bilgisi', description: 'Yaş, cinsiyet ve temel bilgiler' },
    { id: 2, title: 'Şikayet', description: 'Ana şikayet ve hastalık özeti' },
    { id: 3, title: 'Vitaller', description: 'Vital bulgular (opsiyonel)' },
    { id: 4, title: 'Özet', description: 'Bilgileri kontrol edin' }
  ];

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const nextStep = () => {
    if (currentStep < steps.length) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleSubmit = async () => {
    setIsLoading(true);
    try {
      await onStartAssessment(formData);
    } catch (error) {
      console.error('Error submitting assessment:', error);
      setIsLoading(false);
    }
  };

  const renderStepContent = () => {
    switch (currentStep) {
      case 1:
        return (
          <div className="step-content">
            <div className="form-group">
              <label className="form-label">Ad Soyad *</label>
              <input 
                type="text"
                className="form-input"
                placeholder="Adınızı ve soyadınızı girin"
                value={formData.name}
                onChange={(e) => handleInputChange('name', e.target.value)}
                required
              />
            </div>
            <div className="form-row">
              <div className="form-group">
                <label className="form-label">Yaş *</label>
                <input 
                  type="number"
                  className="form-input"
                  placeholder="Yaşınız"
                  min="0"
                  max="120"
                  value={formData.age}
                  onChange={(e) => handleInputChange('age', e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <label className="form-label">Cinsiyet *</label>
                <select 
                  className="form-select"
                  value={formData.gender}
                  onChange={(e) => handleInputChange('gender', e.target.value)}
                  required
                >
                  <option value="F">Kadın</option>
                  <option value="M">Erkek</option>
                </select>
              </div>
            </div>
            <div className="form-group">
              <label className="form-label">Gebelik</label>
              <select 
                className="form-select"
                value={formData.pregnancy}
                onChange={(e) => handleInputChange('pregnancy', e.target.value)}
              >
                <option value="any">Seçilmedi</option>
                <option value="positive">Pozitif</option>
                <option value="negative">Negatif</option>
              </select>
            </div>
          </div>
        );
      
      case 2:
        return (
          <div className="step-content">
            <div className="form-group">
              <label className="form-label">Şikâyet metni *</label>
              <textarea 
                className="form-textarea"
                placeholder="örn. göğsümde baskı var, nefes almakta zorlanıyorum"
                value={formData.complaint}
                onChange={(e) => handleInputChange('complaint', e.target.value)}
                rows={4}
                required
              />
            </div>
            <div className="form-group">
              <label className="form-label">Hastalık Özeti</label>
              <input 
                className="form-input"
                placeholder="Göğüs ağrısı"
                value={formData.chief}
                onChange={(e) => handleInputChange('chief', e.target.value)}
              />
            </div>
          </div>
        );
      
      case 3:
        return (
          <div className="step-content">
            <div className="form-group">
              <label className="form-label">Vitaller (JSON, opsiyonel)</label>
              <textarea 
                className="form-textarea mono"
                placeholder='örn. {"HR": 110, "RR": 24, "SpO2": 93, "SBP": 118, "Temp": 37.8}'
                value={formData.vitals}
                onChange={(e) => handleInputChange('vitals', e.target.value)}
                rows={4}
              />
              <div className="form-help">
                <small>Vital bulgularınızı JSON formatında girebilirsiniz. Boş bırakabilirsiniz.</small>
              </div>
            </div>
          </div>
        );
      
      case 4:
        return (
          <div className="step-content">
            <div className="review-section">
              <h4>Bilgilerinizi Kontrol Edin</h4>
              <div className="review-grid">
                <div className="review-item">
                  <span className="review-label">Yaş:</span>
                  <span className="review-value">{formData.age || 'Belirtilmedi'}</span>
                </div>
                <div className="review-item">
                  <span className="review-label">Cinsiyet:</span>
                  <span className="review-value">{formData.sex === 'F' ? 'Kadın' : formData.sex === 'M' ? 'Erkek' : 'Belirtilmedi'}</span>
                </div>
                <div className="review-item">
                  <span className="review-label">Gebelik:</span>
                  <span className="review-value">
                    {formData.pregnancy === 'positive' ? 'Pozitif' : 
                     formData.pregnancy === 'negative' ? 'Negatif' : 'Seçilmedi'}
                  </span>
                </div>
                <div className="review-item full-width">
                  <span className="review-label">Şikâyet metni:</span>
                  <span className="review-value">{formData.complaint || 'Belirtilmedi'}</span>
                </div>
                <div className="review-item">
                  <span className="review-label">Hastalık Özeti:</span>
                  <span className="review-value">{formData.chief || 'Belirtilmedi'}</span>
                </div>
                <div className="review-item full-width">
                  <span className="review-label">Vitaller:</span>
                  <span className="review-value">{formData.vitals || 'Belirtilmedi'}</span>
                </div>
              </div>
            </div>
          </div>
        );
      
      default:
        return null;
    }
  };

  const isStepValid = () => {
    switch (currentStep) {
      case 1:
        return formData.name.trim() && formData.age && formData.gender;
      case 2:
        return formData.complaint.trim();
      case 3:
        return true; // Optional step
      case 4:
        return true; // Review step
      default:
        return false;
    }
  };

  return (
    <div className="patient-entry">
      <div className="entry-header">
        <div className="header-content">
          <div className="logo-section">
            <div className="logo-icon">
              <svg width="40" height="40" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M12 2L2 7L12 12L22 7L12 2Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M2 17L12 22L22 17" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M2 12L12 17L22 12" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </div>
            <div className="brand-text">
              <h1>E-Triaj Sistemi</h1>
              <p>Hızlı ve güvenli sağlık değerlendirmesi</p>
            </div>
          </div>
          <div style={{ textAlign: 'center', marginTop: '1rem' }}>
            <a href="/test" style={{ padding: '0.5rem 1rem', backgroundColor: '#1B263B', color: 'white', textDecoration: 'none', borderRadius: '8px' }}>
              Test Sayfası
            </a>
          </div>
        </div>
      </div>

      <div className="entry-content">
        <div className="entry-card">
          <div className="card-header">
            <h2>Hasta Bilgi Formu</h2>
            <p>Lütfen bilgilerinizi eksiksiz doldurun</p>
          </div>

          {/* Step Progress */}
          <div className="step-progress">
            {steps.map((step, index) => (
              <div key={step.id} className={`step ${currentStep >= step.id ? 'active' : ''} ${currentStep > step.id ? 'completed' : ''}`}>
                <div className="step-number">
                  {currentStep > step.id ? (
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                      <polyline points="20,6 9,17 4,12" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                    </svg>
                  ) : (
                    step.id
                  )}
                </div>
                <div className="step-info">
                  <div className="step-title">{step.title}</div>
                  <div className="step-description">{step.description}</div>
                </div>
              </div>
            ))}
          </div>

          <div className="card-body">
            {renderStepContent()}
          </div>

          <div className="card-footer">
            <div className="step-actions">
              {currentStep > 1 && (
                <button className="btn btn-outline" onClick={prevStep}>
                  <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <polyline points="15,18 9,12 15,6" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                  </svg>
                  Geri
                </button>
              )}
              
              <div className="step-actions-right">
                {currentStep < steps.length ? (
                  <button 
                    className="btn btn-primary" 
                    onClick={nextStep}
                    disabled={!isStepValid()}
                  >
                    İleri
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                      <polyline points="9,18 15,12 9,6" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                    </svg>
                  </button>
                ) : (
                  <button 
                    className="btn btn-primary" 
                    onClick={handleSubmit}
                    disabled={!isStepValid() || isLoading}
                  >
                    {isLoading ? 'Değerlendiriliyor...' : 'Değerlendirmeyi Başlat'}
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                      <polygon points="5,3 19,12 5,21" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                    </svg>
                  </button>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PatientEntry;
