import React from "react";
import { QRCodeSVG } from "qrcode.react";
import "./QrCodeGenerator.css";

interface TriageLabelProps {
  caseId: string;
  status?: string;
  area?: string;
  department?: string;
  baseUrl?: string;
  path?: string;
}

export default function TriageLabel({
  caseId,
  status,
  area,
  department,
  baseUrl = "https://aitriaj.com",
  path = "/triage/case_id",
}: TriageLabelProps) {
  const qrPath = path.replace("case_id", caseId || "no-id");
  const qrLink = `${baseUrl}${qrPath}`;

  return (
    <div className="w-80 border rounded-lg shadow p-4 bg-white">
      <h2 className="text-center text-lg font-bold mb-2">
        TRİAJ ETİKET ÇIKTISI
      </h2>
      <div className="flex justify-between items-center mb-4">
        <div className="text-xl font-semibold">case_id: {caseId}</div>
        <div className="w-16 h-16">
          <QRCodeSVG value={qrLink} size={64} />
        </div>
      </div>
      <p className="text-sm mb-4">
        {status ||
          "Hastanın 'bayılma' ifadesi ciddi ve acil müdahale gerektiren durumları düşündürür."}
      </p>
      <div className="flex justify-between items-center">
        <div className="border rounded px-3 py-1 text-sm">
          {area || "ALAN BİLGİSİ"}
        </div>
        <div className="font-bold text-base">{department || "BÖLÜM"}</div>
      </div>
    </div>
  );
}
