import React, { useState, useCallback } from 'react';
import './App.css';
import InputSection from './components/InputSection';
import ResultsDisplay from './components/ResultsDisplay';
import { TriageInput, TriageStartResponse } from './types/TriageTypes';
import { triageApi } from './api/triageApi';

function App() {
  const [isLoading, setIsLoading] = useState(false);
  const [triageResults, setTriageResults] = useState<TriageStartResponse[]>([]);

  const handleSubmit = useCallback(async (input: TriageInput) => {
    setIsLoading(true);

    try {
      const response = await triageApi.startTriage(input);

      // Add to results list
      setTriageResults(prev => [...prev, response]);

    } catch (error) {
      console.error('Triage başlatma hatası:', error);
    } finally {
      setIsLoading(false);
    }
  }, []);



  return (
    <div className="App">
      <main className="main-content">
        <div className="dashboard-container">
          <div className="input-panel">
            <InputSection
              onSubmit={handleSubmit}
              isLoading={isLoading}
            />
          </div>

          <ResultsDisplay results={triageResults} isLoading={isLoading} />
        </div>
      </main>
    </div>
  );
}

export default App;
