import React, { useState, useEffect } from 'react';
import './InputSection.css';
import { TriageInput, TriageState } from '../types/TriageTypes';

interface InputSectionProps {
  onSubmit: (data: TriageInput) => void;
  onSendAnswer: (answers: Record<string, string>, done?: boolean) => void;
  onReset: () => void;
  triageState: TriageState;
}

const InputSection: React.FC<InputSectionProps> = ({ 
  onSubmit, 
  onSendAnswer, 
  onReset, 
  triageState 
}) => {
  const [formData, setFormData] = useState({
    name: '',
    age: 30,
    sex: 'F',
    complaint_text: ''
  });
  const [currentAnswers, setCurrentAnswers] = useState<Record<string, string>>({});
  const [error, setError] = useState('');
  const [currentStep, setCurrentStep] = useState<'initial' | 'questions'>('initial');

  // Reset form when triage is reset
  useEffect(() => {
    if (!triageState.caseId) {
      setCurrentStep('initial');
      setCurrentAnswers({});
      setFormData({
        name: '',
        age: 30,
        sex: 'F',
        complaint_text: ''
      });
    }
  }, [triageState.caseId]);

  // Move to questions step when triage starts
  useEffect(() => {
    if (triageState.caseId && triageState.triage) {
      setCurrentStep('questions');
    }
  }, [triageState.caseId, triageState.triage]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleAnswerChange = (question: string, answer: string) => {
    setCurrentAnswers(prev => ({
      ...prev,
      [question]: answer
    }));
  };

  const handleInitialSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.name || !formData.age || !formData.sex || !formData.complaint_text) {
      setError('Lütfen tüm alanları doldurun');
      return;
    }

    setError('');

    const triageInput: TriageInput = {
      name: formData.name,
      age: parseInt(formData.age.toString()),
      sex: formData.sex as 'male' | 'female',
      complaint_text: formData.complaint_text
    };

    onSubmit(triageInput);
  };

  const handleQuestionSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    onSendAnswer(currentAnswers, false);
    setCurrentAnswers({});
  };

  const handleFinishTriage = () => {
    onSendAnswer(currentAnswers, true);
    setCurrentAnswers({});
  };

  const handleReset = () => {
    setFormData({
      name: '',
      age: 30,
      sex: 'F',
      complaint_text: ''
    });
    setCurrentAnswers({});
    setError('');
    setCurrentStep('initial');
    onReset();
  };

  const getQuestionsArray = () => {
    if (!triageState.triage?.questions_to_ask_next) return [];

    const questions = triageState.triage.questions_to_ask_next;

    if (Array.isArray(questions)) {
      return questions;
    }

    const allQuestions: string[] = [];
    if (questions.primary) allQuestions.push(...questions.primary);
    if (questions.secondary) allQuestions.push(...questions.secondary);

    return allQuestions;
  };

  const questionsArray = getQuestionsArray();
  const hasQuestions = questionsArray.length > 0;

  if (currentStep === 'questions' && triageState.caseId) {
    return (
      <section className="card input-section">
        <div className="card-header">
          <h3>Ek Sorular</h3>
          <p>Daha doğru değerlendirme için lütfen aşağıdaki soruları cevaplayın</p>
        </div>

        <div className="card-body">
          {triageState.error && (
            <div className="error-message">
              {triageState.error}
            </div>
          )}

          {hasQuestions ? (
            <form onSubmit={handleQuestionSubmit} className="questions-form">
              <div className="questions-list">
                {questionsArray.map((question, index) => (
                  <div key={index} className="question-group">
                    <label htmlFor={`question-${index}`} className="question-label">
                      {question}
                    </label>
                    <textarea
                      id={`question-${index}`}
                      value={currentAnswers[question] || ''}
                      onChange={(e) => handleAnswerChange(question, e.target.value)}
                      placeholder="Cevabınızı buraya yazın..."
                      rows={3}
                      className="question-input"
                    />
                  </div>
                ))}
              </div>

              <div className="form-actions">
                <button 
                  type="submit" 
                  className="btn btn-primary"
                  disabled={triageState.isLoading}
                >
                  {triageState.isLoading ? 'Gönderiliyor...' : 'Cevapları Gönder'}
                </button>

                <button 
                  type="button" 
                  className="btn btn-success"
                  onClick={handleFinishTriage}
                  disabled={triageState.isLoading}
                >
                  Triage'ı Tamamla
                </button>

                <button 
                  type="button" 
                  className="btn btn-outline"
                  onClick={handleReset}
                >
                  Yeniden Başla
                </button>
              </div>
            </form>
          ) : (
            <div className="no-questions">
              <p>Tüm sorular tamamlandı. Triage değerlendirmesi hazır.</p>
              <div className="form-actions">
                <button 
                  type="button" 
                  className="btn btn-outline"
                  onClick={handleReset}
                >
                  Yeni Değerlendirme
                </button>
              </div>
            </div>
          )}
        </div>
      </section>
    );
  }

  return (
    <section className="card input-section">
      <div className="card-header">
        <h3>Hasta Bilgileri</h3>
        <p>Triage değerlendirmesi için hasta bilgilerini girin</p>
      </div>

      <div className="card-body">
        {error && (
          <div className="error-message">
            {error}
          </div>
        )}

        {triageState.error && (
          <div className="error-message">
            {triageState.error}
          </div>
        )}

        <form onSubmit={handleInitialSubmit} className="triage-form">
          <div className="form-group">
            <label htmlFor="name">Hasta Adı</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              placeholder="Hasta adını girin"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="age">Yaş</label>
            <input
              type="number"
              id="age"
              name="age"
              value={formData.age}
              onChange={handleInputChange}
              min="0"
              max="120"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="sex">Cinsiyet</label>
            <select
              id="sex"
              name="sex"
              value={formData.sex}
              onChange={handleInputChange}
              required
            >
              <option value="F">Kadın</option>
              <option value="M">Erkek</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="complaint_text">Şikayet</label>
            <textarea
              id="complaint_text"
              name="complaint_text"
              value={formData.complaint_text}
              onChange={handleInputChange}
              placeholder="Hastanın şikayetini detaylı olarak açıklayın"
              rows={4}
              required
            />
          </div>

          <div className="form-actions">
            <button 
              type="submit" 
              className="btn btn-primary"
              disabled={triageState.isLoading}
            >
              {triageState.isLoading ? 'Değerlendiriliyor...' : 'Triage Başlat'}
            </button>

            <button 
              type="button" 
              className="btn btn-outline"
              onClick={handleReset}
            >
              Temizle
            </button>
          </div>
        </form>
      </div>
    </section>
  );
};

export default InputSection;
