import React, { useState } from 'react';
import './PatientEntry.css';

interface PatientEntryProps {
  onStartAssessment: (patientData: any) => Promise<void>;
}

const PatientEntry: React.FC<PatientEntryProps> = ({ onStartAssessment }) => {
  const [currentStep, setCurrentStep] = useState(1);
  const [isLoading, setIsLoading] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    age: '30',
    gender: 'F',
    sex: 'F',
    complaint: '',
    pregnancy: 'any',
    chief: '',
    vitals: ''
  });

  const steps = [
    { id: 1, title: 'Hasta Bilgisi', description: 'Yaş, cinsiyet ve temel bilgiler' },
    { id: 2, title: 'Şikayet', description: 'Ana şikayet ve hastalık özeti' },
    { id: 3, title: 'Vitaller', description: 'Vital bulgular (opsiyonel)' },
    { id: 4, title: 'Özet', description: 'Bilgileri kontrol edin' }
  ];

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const nextStep = () => {
    if (currentStep < steps.length) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleSubmit = async () => {
    setIsLoading(true);
    try {
      await onStartAssessment(formData);
    } catch (error) {
      console.error('Error submitting assessment:', error);
      setIsLoading(false);
    }
  };

  const renderStepContent = () => {
    switch (currentStep) {
      case 1:
        return (
          <div className="step-content">
            <div className="form-group">
              <label className="form-label">Ad Soyad *</label>
              <input 
                type="text"
                className="form-input"
                placeholder="Adınızı ve soyadınızı girin"
                value={formData.name}
                onChange={(e) => handleInputChange('name', e.target.value)}
                required
              />
            </div>
            <div className="form-row">
              <div className="form-group">
                <label className="form-label">Yaş *</label>
                <input 
                  type="number"
                  className="form-input"
                  placeholder="Yaşınız"
                  min="0"
                  max="120"
                  value={formData.age}
                  onChange={(e) => handleInputChange('age', e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <label className="form-label">Cinsiyet *</label>
                <select 
                  className="form-select"
                  value={formData.gender}
                  onChange={(e) => handleInputChange('gender', e.target.value)}
                  required
                >
                  <option value="F">Kadın</option>
                  <option value="M">Erkek</option>
                </select>
              </div>
            </div>
            <div className="form-group">
              <label className="form-label">Gebelik</label>
              <select 
                className="form-select"
                value={formData.pregnancy}
                onChange={(e) => handleInputChange('pregnancy', e.target.value)}
              >
                <option value="any">Seçilmedi</option>
                <option value="positive">Pozitif</option>
                <option value="negative">Negatif</option>
              </select>
            </div>
          </div>
        );
      
      case 2:
        return (
          <div className="step-content">
            <div className="form-group">
              <label className="form-label">Şikâyet metni *</label>
              <textarea 
                className="form-textarea"
                placeholder="örn. göğsümde baskı var, nefes almakta zorlanıyorum"
                value={formData.complaint}
                onChange={(e) => handleInputChange('complaint', e.target.value)}
                rows={4}
                required
              />
            </div>
            <div className="form-group">
              <label className="form-label">Hastalık Özeti</label>
              <input 
                className="form-input"
                placeholder="Göğüs ağrısı"
                value={formData.chief}
                onChange={(e) => handleInputChange('chief', e.target.value)}
              />
            </div>
          </div>
        );
      
      case 3:
        return (
          <div className="step-content">
            <div className="form-group">
              <label className="form-label">Vitaller (JSON, opsiyonel)</label>
              <textarea 
                className="form-textarea mono"
                placeholder='örn. {"HR": 110, "RR": 24, "SpO2": 93, "SBP": 118, "Temp": 37.8}'
                value={formData.vitals}
                onChange={(e) => handleInputChange('vitals', e.target.value)}
                rows={4}
              />
              <div className="form-help">
                <small>Vital bulgularınızı JSON formatında girebilirsiniz. Boş bırakabilirsiniz.</small>
              </div>
            </div>
          </div>
        );
      
      case 4:
        return (
          <div className="step-content">
            <div className="review-section">
              <h4>Bilgilerinizi Kontrol Edin</h4>
              <div className="review-grid">
                <div className="review-item">
                  <span className="review-label">Ad Soyad:</span>
                  <span className="review-value">{formData.name || 'Belirtilmedi'}</span>
                </div>
                <div className="review-item">
                  <span className="review-label">Yaş:</span>
                  <span className="review-value">{formData.age || 'Belirtilmedi'}</span>
                </div>
                <div className="review-item">
                  <span className="review-label">Cinsiyet:</span>
                  <span className="review-value">{formData.gender === 'F' ? 'Kadın' : formData.gender === 'M' ? 'Erkek' : 'Belirtilmedi'}</span>
                </div>
                <div className="review-item">
                  <span className="review-label">Gebelik:</span>
                  <span className="review-value">
                    {formData.pregnancy === 'positive' ? 'Pozitif' : 
                     formData.pregnancy === 'negative' ? 'Negatif' : 'Seçilmedi'}
                  </span>
                </div>
                <div className="review-item full-width">
                  <span className="review-label">Şikâyet metni:</span>
                  <span className="review-value">{formData.complaint || 'Belirtilmedi'}</span>
                </div>
                <div className="review-item">
                  <span className="review-label">Hastalık Özeti:</span>
                  <span className="review-value">{formData.chief || 'Belirtilmedi'}</span>
                </div>
                <div className="review-item full-width">
                  <span className="review-label">Vitaller:</span>
                  <span className="review-value">{formData.vitals || 'Belirtilmedi'}</span>
                </div>
              </div>
            </div>
          </div>
        );
      
      default:
        return null;
    }
  };

  const isStepValid = () => {
    switch (currentStep) {
      case 1:
        return formData.name.trim() && formData.age && formData.gender;
      case 2:
        return formData.complaint.trim();
      case 3:
        return true; // Optional step
      case 4:
        return true; // Review step
      default:
        return false;
    }
  };

  return (
    <div className="patient-entry">
      <main>
        {/* SOL: Giriş & Soru Akışı */}
        <section className="card input-section">
          <div className="info-card">
            <h3>Triaj Değerlendirme</h3>
            <p>Hasta bilgilerini girin ve sistemin sorularını yanıtlayın</p>
          </div>
          
          <h2>Hasta Bilgisi</h2>

          {/* Form Content */}
          <div className="form-content">
            {renderStepContent()}
          </div>

          <div className="hr"></div>
          <div className="split">
            <button className="btn" onClick={handleSubmit} disabled={!isStepValid() || isLoading}>
              {isLoading ? 'Değerlendiriliyor...' : 'Başla'}
            </button>
            <button className="btn secondary" onClick={() => window.location.reload()}>
              Sıfırla
            </button>
          </div>

          <div className="small muted" style={{marginTop: '8px'}}>
            {isLoading && 'Değerlendirme başlatılıyor...'}
          </div>

          <div className="hr"></div>

          <h2>Takip Soruları</h2>
          <div className="qa" style={{display: 'none'}}>
            <div style={{marginBottom: '8px', fontWeight: '500'}}>Soru burada görünecek</div>
            <textarea placeholder="Cevabınızı yazın..."></textarea>
            <div className="split" style={{marginTop: '8px'}}>
              <button className="btn">Yanıtla (sonraki soru)</button>
              <button className="btn secondary">Bu soruyu atla</button>
              <button className="btn success">Bitir</button>
            </div>
            <div className="small muted" style={{marginTop: '6px'}}>
              Soru bilgisi burada görünecek
            </div>
          </div>

          <div className="small muted" style={{display: 'none'}}>
            Şu an sorulacak başka soru yok.
          </div>

          <div className="hr"></div>
          <div>
            <h2>Oturum Bilgisi</h2>
            <div className="small">Case ID: <span className="pill">-</span></div>
            <div className="small">Son kayıt: <span>-</span></div>
          </div>
        </section>

        {/* SAĞ: Canlı Sonuç Paneli */}
        <aside className="card results-section">
          <h2>Değerlendirme Sonuçları</h2>
          
          {/* Durum Özeti */}
          <div className="result-summary">
            <div className="status-indicator">
              <span className="dot green"></span>
              <span className="status-text">Değerlendirme Durumu</span>
            </div>
            <div className="badges-container">
              <span className="badge primary">ESI: -</span>
              <span className="badge secondary">Öncelik: -</span>
              <span className="badge tertiary">Bölüm: -</span>
            </div>
          </div>

          {/* Değerlendirme Açıklaması */}
          <div className="rationale-section">
            <h3>Değerlendirme Açıklaması</h3>
            <div className="rationale-content">
              Henüz değerlendirme yapılmadı. Hasta bilgilerini girin ve değerlendirmeyi başlatın.
            </div>
          </div>

          <div className="hr"></div>
          
          {/* Kritik Bulgular */}
          <div className="section-block">
            <h3>Kritik Bulgular (Red Flags)</h3>
            <ul className="findings-list"></ul>
          </div>

          <div className="hr"></div>
          
          {/* İlk Müdahaleler */}
          <div className="section-block">
            <h3>İlk Müdahaleler</h3>
            <ul></ul>
          </div>

          <div className="hr"></div>
          <div>
            <strong>Sorulacak ek sorular (güncel)</strong>
            <ul></ul>
          </div>
        </aside>
      </main>
    </div>
  );
};

export default PatientEntry;
