import React, { useState } from 'react';
import './InputSection.css';
import { TriageInput } from '../types/TriageTypes';

interface InputSectionProps {
  onSubmit: (data: TriageInput) => void;
  isLoading?: boolean;
}

const InputSection: React.FC<InputSectionProps> = ({ onSubmit, isLoading = false }) => {
  const [formData, setFormData] = useState({
    name: '',
    age: 30,
    sex: 'F',
    complaint_text: ''
  });
  const [error, setError] = useState('');

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.name || !formData.age || !formData.sex || !formData.complaint_text) {
      setError('Lütfen tüm alanları doldurun');
      return;
    }

    setError('');

    const triageInput: TriageInput = {
      name: formData.name,
      age: parseInt(formData.age.toString()),
      sex: formData.sex as 'male' | 'female',
      complaint_text: formData.complaint_text
    };

    onSubmit(triageInput);
  };

  const handleReset = () => {
    setFormData({
      name: '',
      age: 30,
      sex: 'F',
      complaint_text: ''
    });
    setError('');
  };

  return (
    <div className="input-section">
      <div className="header">
        <h1>Triage Değerlendirme Sistemi</h1>
        <p>Hasta bilgilerini girerek triage değerlendirmesi başlatın</p>
      </div>

      <form onSubmit={handleSubmit} className="triage-form">
        <div className="form-group">
          <label htmlFor="name">Hasta Adı</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            placeholder="Hasta adını girin"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="age">Yaş</label>
          <input
            type="number"
            id="age"
            name="age"
            value={formData.age}
            onChange={handleInputChange}
            min="0"
            max="120"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="sex">Cinsiyet</label>
          <select
            id="sex"
            name="sex"
            value={formData.sex}
            onChange={handleInputChange}
            required
          >
            <option value="F">Kadın</option>
            <option value="M">Erkek</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="complaint_text">Şikayet</label>
          <textarea
            id="complaint_text"
            name="complaint_text"
            value={formData.complaint_text}
            onChange={handleInputChange}
            placeholder="Hastanın şikayetini detaylı olarak açıklayın"
            rows={4}
            required
          />
        </div>

        {error && (
          <div className="error-message">
            {error}
          </div>
        )}

        <div className="form-actions">
          <button 
            type="submit" 
            className="btn btn-primary"
            disabled={isLoading}
          >
            {isLoading ? 'Değerlendiriliyor...' : 'Triage Başlat'}
          </button>

          <button 
            type="button" 
            className="btn btn-outline"
            onClick={handleReset}
          >
            Temizle
          </button>
        </div>
      </form>
    </div>
  );
};

export default InputSection;
