import React, { useState } from 'react';
import './App.css';
import Header from './components/Header';
import InputSection from './components/InputSection';
import ResultsSection from './components/ResultsSection';
import { TriageInput, TriageState, AnswerBody } from './types/TriageTypes';
import { startTriage, sendAnswer } from './services/triageApi';

function App() {
  const [triageState, setTriageState] = useState<TriageState>({
    caseId: null,
    currentQuestion: null,
    remainingQuestions: 0,
    triage: null,
    filePath: null,
    isLoading: false,
    error: null
  });

  const handleTriageStart = async (input: TriageInput) => {
    setTriageState(prev => ({ ...prev, isLoading: true, error: null }));

    try {
      const response = await startTriage(input);
      setTriageState({
        caseId: response.case_id,
        currentQuestion: null,
        remainingQuestions: Array.isArray(response.questions_to_ask_next) 
          ? response.questions_to_ask_next.length 
          : (response.questions_to_ask_next?.primary?.length || 0) + (response.questions_to_ask_next?.secondary?.length || 0),
        triage: response.triage,
        filePath: response.file_path,
        isLoading: false,
        error: null
      });
    } catch (error) {
      setTriageState(prev => ({
        ...prev,
        isLoading: false,
        error: error instanceof Error ? error.message : 'Bir hata oluştu'
      }));
    }
  };

  const handleSendAnswer = async (answers: Record<string, string>, done: boolean = false) => {
    if (!triageState.caseId) return;

    setTriageState(prev => ({ ...prev, isLoading: true, error: null }));

    try {
      const answerBody: AnswerBody = {
        answers,
        done
      };

      const response = await sendAnswer(triageState.caseId, answerBody);
      setTriageState(prev => ({
        ...prev,
        triage: response.triage,
        remainingQuestions: Array.isArray(response.questions_to_ask_next) 
          ? response.questions_to_ask_next.length 
          : (response.questions_to_ask_next?.primary?.length || 0) + (response.questions_to_ask_next?.secondary?.length || 0),
        isLoading: false
      }));
    } catch (error) {
      setTriageState(prev => ({
        ...prev,
        isLoading: false,
        error: error instanceof Error ? error.message : 'Bir hata oluştu'
      }));
    }
  };

  const handleReset = () => {
    setTriageState({
      caseId: null,
      currentQuestion: null,
      remainingQuestions: 0,
      triage: null,
      filePath: null,
      isLoading: false,
      error: null
    });
  };

  return (
    <div className="App">
      <Header />
      <main className="main-content">
        <InputSection 
          onSubmit={handleTriageStart}
          onSendAnswer={handleSendAnswer}
          onReset={handleReset}
          triageState={triageState}
        />
        <ResultsSection triageState={triageState} />
      </main>
    </div>
  );
}

export default App;
