import React, { useState } from "react";
import TriageLabel from "./QrCodeGenerator";
import "./QrCodeTestPage.css";

export default function QrCodeTestPage() {
  const [testCaseId, setTestCaseId] = useState("TEST-001");
  const [testStatus, setTestStatus] = useState("Test durumu: Hastanın durumu stabil");
  const [testArea, setTestArea] = useState("ACİL SERVİS");
  const [testDepartment, setTestDepartment] = useState("GENEL CERRAHİ");
  const [testBaseUrl, setTestBaseUrl] = useState("https://aitriaj.com");
  const [testPath, setTestPath] = useState("/triage/case_id");

  const testCases = [
    {
      id: "TEST-001",
      status: "Hastanın 'bayılma' ifadesi ciddi ve acil müdahale gerektiren durumları düşündürür.",
      area: "ACİL SERVİS",
      department: "GENEL CERRAHİ"
    },
    {
      id: "TEST-002", 
      status: "Hasta bilinçli, vital bulgular stabil",
      area: "POLİKLİNİK",
      department: "İÇ HASTALIKLARI"
    },
    {
      id: "TEST-003",
      status: "Acil müdahale gerekli - kritik durum",
      area: "YOĞUN BAKIM",
      department: "ANESTEZİ VE REANİMASYON"
    }
  ];

  const handleTestCaseSelect = (testCase: any) => {
    setTestCaseId(testCase.id);
    setTestStatus(testCase.status);
    setTestArea(testCase.area);
    setTestDepartment(testCase.department);
  };

  return (
    <div className="qr-test-page">
      <div className="qr-test-container">
        <h1 className="qr-test-title">
          QR Kod Test Sayfası
        </h1>
        
        <div className="qr-test-grid">
          {/* Sol taraf - Kontroller */}
          <div className="qr-test-controls">
            <h2 className="qr-test-controls-title">
              Test Parametreleri
            </h2>
            
            {/* Hazır Test Durumları */}
            <div className="qr-test-scenarios">
              <h3 className="qr-test-scenarios-title">
                Hazır Test Durumları
              </h3>
              <div className="qr-test-scenario-list">
                {testCases.map((testCase, index) => (
                  <button
                    key={index}
                    onClick={() => handleTestCaseSelect(testCase)}
                    className={`qr-test-scenario-btn ${
                      testCaseId === testCase.id ? 'active' : ''
                    }`}
                  >
                    <div className="qr-test-scenario-id">{testCase.id}</div>
                    <div className="qr-test-scenario-details">{testCase.area} - {testCase.department}</div>
                  </button>
                ))}
              </div>
            </div>

            {/* Manuel Parametreler */}
            <div className="qr-test-form">
              <div className="qr-test-form-group">
                <label className="qr-test-form-label">
                  Case ID
                </label>
                <input
                  type="text"
                  value={testCaseId}
                  onChange={(e) => setTestCaseId(e.target.value)}
                  className="qr-test-form-input"
                />
              </div>

              <div className="qr-test-form-group">
                <label className="qr-test-form-label">
                  Durum
                </label>
                <textarea
                  value={testStatus}
                  onChange={(e) => setTestStatus(e.target.value)}
                  rows={3}
                  className="qr-test-form-textarea"
                />
              </div>

              <div className="qr-test-form-group">
                <label className="qr-test-form-label">
                  Alan
                </label>
                <input
                  type="text"
                  value={testArea}
                  onChange={(e) => setTestArea(e.target.value)}
                  className="qr-test-form-input"
                />
              </div>

              <div className="qr-test-form-group">
                <label className="qr-test-form-label">
                  Bölüm
                </label>
                <input
                  type="text"
                  value={testDepartment}
                  onChange={(e) => setTestDepartment(e.target.value)}
                  className="qr-test-form-input"
                />
              </div>

              <div className="qr-test-form-group">
                <label className="qr-test-form-label">
                  Base URL
                </label>
                <input
                  type="text"
                  value={testBaseUrl}
                  onChange={(e) => setTestBaseUrl(e.target.value)}
                  className="qr-test-form-input"
                />
              </div>

              <div className="qr-test-form-group">
                <label className="qr-test-form-label">
                  Path
                </label>
                <input
                  type="text"
                  value={testPath}
                  onChange={(e) => setTestPath(e.target.value)}
                  className="qr-test-form-input"
                />
              </div>
            </div>
          </div>

          {/* Sağ taraf - QR Kod Önizleme */}
          <div className="qr-test-preview">
            <h2 className="qr-test-preview-title">
              QR Kod Önizleme
            </h2>
            
            <div className="qr-test-preview-container">
              <TriageLabel
                caseId={testCaseId}
                status={testStatus}
                area={testArea}
                department={testDepartment}
                baseUrl={testBaseUrl}
                path={testPath}
              />
            </div>

            {/* QR Kod Bilgileri */}
            <div className="qr-test-info">
              <h3 className="qr-test-info-title">QR Kod Bilgileri:</h3>
              <div className="qr-test-info-list">
                <div className="qr-test-info-item"><strong>URL:</strong> {testBaseUrl}{testPath.replace("case_id", testCaseId)}</div>
                <div className="qr-test-info-item"><strong>Case ID:</strong> {testCaseId}</div>
                <div className="qr-test-info-item"><strong>Alan:</strong> {testArea}</div>
                <div className="qr-test-info-item"><strong>Bölüm:</strong> {testDepartment}</div>
              </div>
            </div>

            {/* Test Butonları */}
            <div className="qr-test-actions">
              <button
                onClick={() => {
                  const qrUrl = `${testBaseUrl}${testPath.replace("case_id", testCaseId)}`;
                  window.open(qrUrl, '_blank');
                }}
                className="qr-test-btn qr-test-btn-primary"
              >
                QR Kod Linkini Aç
              </button>
              
              <button
                onClick={() => {
                  const qrUrl = `${testBaseUrl}${testPath.replace("case_id", testCaseId)}`;
                  navigator.clipboard.writeText(qrUrl);
                  alert('QR kod URL\'si panoya kopyalandı!');
                }}
                className="qr-test-btn qr-test-btn-success"
              >
                URL'yi Panoya Kopyala
              </button>
            </div>
          </div>
        </div>

        {/* Alt kısım - Bilgi */}
        <div className="qr-test-info-card">
          <h3 className="qr-test-info-card-title">Test Sayfası Hakkında</h3>
          <p className="qr-test-info-card-text">
            Bu sayfa QR Kod Generator bileşeninin farklı parametrelerle nasıl çalıştığını test etmek için oluşturulmuştur. 
            Sol taraftaki kontrollerle parametreleri değiştirebilir, sağ tarafta sonucu anlık olarak görebilirsiniz.
          </p>
        </div>
      </div>
    </div>
  );
}
