import React, { useState } from 'react';
import './InputSection.css';
import { TriageState } from '../types/TriageTypes';

interface InputSectionProps {
  triageState: TriageState;
  onStartTriage: (data: any) => void;
  onSendAnswer: (answer: string, skip?: boolean) => void;
  onDone: () => void;
  onReset: () => void;
}

const InputSection: React.FC<InputSectionProps> = ({
  triageState,
  onStartTriage,
  onSendAnswer,
  onDone,
  onReset
}) => {
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    age: 30,
    sex: 'F',
    complaint: '',
    pregnancy: 'any',
    chief: '',
    vitals: ''
  });

  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState<'muted' | 'success' | 'err'>('muted');

  const steps = [
    { id: 1, title: 'Patient Info', description: 'Basic patient information' },
    { id: 2, title: 'Symptoms', description: 'Chief complaint and symptoms' },
    { id: 3, title: 'Vitals', description: 'Vital signs and measurements' },
    { id: 4, title: 'Review', description: 'Review and submit' }
  ];

  const handleInputChange = (field: string, value: string | number) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleStartTriage = async () => {
    if (!formData.complaint.trim()) {
      setMessage('Şikâyet metni boş olamaz.');
      setMessageType('err');
      return;
    }

    // Parse vitals JSON
    let vitals = {};
    if (formData.vitals.trim()) {
      try {
        vitals = JSON.parse(formData.vitals);
      } catch (e) {
        setMessage('Vitaller geçerli JSON değil.');
        setMessageType('err');
        return;
      }
    }

    setMessage('Gönderiliyor...');
    setMessageType('muted');

    const triageData = {
      age: formData.age,
      sex: formData.sex,
      complaint_text: formData.complaint.trim(),
      vitals,
      pregnancy: formData.pregnancy,
      chief: formData.chief.trim() || null
    };

    try {
      await onStartTriage(triageData);
      setMessage('İlk değerlendirme alındı. Sorular sırayla yönlendirilecek.');
      setMessageType('success');
    } catch (err: any) {
      setMessage('Hata: ' + err.message);
      setMessageType('err');
    }
  };

  const handleAnswerSubmit = () => {
    const answer = (document.getElementById('answerInput') as HTMLTextAreaElement)?.value || '';
    onSendAnswer(answer, false);
  };

  const handleSkip = () => {
    onSendAnswer('', true);
  };

  const handleReset = () => {
    setFormData({
      age: 30,
      sex: 'F',
      complaint: '',
      pregnancy: 'any',
      chief: '',
      vitals: ''
    });
    setMessage('');
    setMessageType('muted');
    onReset();
  };

  return (
    <section className="card input-section">
      <div className="info-card">
        <h3>Triaj Değerlendirme</h3>
        <p>Hasta bilgilerini girin ve sistemin sorularını yanıtlayın</p>
      </div>
      
      <h2>Hasta Bilgisi</h2>

      <div className="row">
        <div>
          <label>Yaş</label>
          <input 
            type="number" 
            min="0" 
            value={formData.age}
            onChange={(e) => handleInputChange('age', parseInt(e.target.value) || 0)}
          />
        </div>
        <div>
          <label>Cinsiyet</label>
          <select 
            value={formData.sex}
            onChange={(e) => handleInputChange('sex', e.target.value)}
          >
            <option value="F">Kadın</option>
            <option value="M">Erkek</option>
          </select>
        </div>
      </div>

      <label>Şikâyet metni</label>
      <textarea 
        placeholder="örn. göğsümde baskı var, nefes almakta zorlanıyorum"
        value={formData.complaint}
        onChange={(e) => handleInputChange('complaint', e.target.value)}
      />

      <div className="grid2">
        <div>
          <label>Gebelik</label>
          <select 
            value={formData.pregnancy}
            onChange={(e) => handleInputChange('pregnancy', e.target.value)}
          >
            <option value="any">Seçilmedi</option>
            <option value="positive">Pozitif</option>
            <option value="negative">Negatif</option>
          </select>
        </div>
        <div>
          <label>Hastalık Özeti</label>
          <input 
            placeholder="Göğüs ağrısı"
            value={formData.chief}
            onChange={(e) => handleInputChange('chief', e.target.value)}
          />
        </div>
      </div>

      <label>Vitaller (JSON, opsiyonel)</label>
      <textarea 
        className="mono" 
        placeholder='örn. {"HR": 110, "RR": 24, "SpO2": 93, "SBP": 118, "Temp": 37.8}'
        value={formData.vitals}
        onChange={(e) => handleInputChange('vitals', e.target.value)}
      />

      <div className="hr"></div>
      <div className="split">
        <button className="btn" onClick={handleStartTriage}>Başla</button>
        <button className="btn secondary" onClick={handleReset}>Sıfırla</button>
      </div>

      <div className={`small ${messageType}`} style={{marginTop: '8px'}}>
        {message}
      </div>

      <div className="hr"></div>

      <h2>Takip Soruları</h2>
      <div className="qa" style={{display: triageState.currentQuestion ? 'block' : 'none'}}>
        <div style={{marginBottom: '8px', fontWeight: 500}}>
          Soru: {triageState.currentQuestion}
        </div>
        <textarea id="answerInput" placeholder="Cevabınızı yazın..." />
        <div className="split" style={{marginTop: '8px'}}>
          <button className="btn" onClick={handleAnswerSubmit}>Yanıtla (sonraki soru)</button>
          <button className="btn secondary" onClick={handleSkip}>Bu soruyu atla</button>
          <button className="btn success" onClick={onDone}>Bitir</button>
        </div>
        <div className="small muted" style={{marginTop: '6px'}}>
          {triageState.remainingQuestions} soru daha var
        </div>
      </div>

      <div className="small muted" style={{display: !triageState.currentQuestion && triageState.caseId ? 'block' : 'none'}}>
        Şu an sorulacak başka soru yok.
      </div>

      <div className="hr"></div>
      <div>
        <h2>Oturum Bilgisi</h2>
        <div className="small">Case ID: <span className="pill">{triageState.caseId || '-'}</span></div>
        <div className="small">Son kayıt: <span>{triageState.filePath || '-'}</span></div>
      </div>
    </section>
  );
};

export default InputSection;
